#include &lt;pybind11/pybind11.h&gt;#include &lt;string&gt;namespace py = pybind11;// 重载函数int add(int a, int b) &#123;    return a + b;&#125;double add(double a, double b) &#123;    return a + b;&#125;std::string add(const std::string&amp; a, const std::string&amp; b) &#123;    return a + b;&#125;PYBIND11_MODULE(example, m) &#123;    // 绑定重载函数    m.def(&quot;add&quot;, py::overload_cast&lt;int, int&gt;(&amp;add), &quot;整数加法&quot;);    m.def(&quot;add&quot;, py::overload_cast&lt;double, double&gt;(&amp;add), &quot;浮点数加法&quot;);    m.def(&quot;add&quot;, py::overload_cast&lt;const std::string&amp;, const std::string&amp;&gt;(&amp;add), &quot;字符串连接&quot;);&#125;
