#include &lt;pybind11/pybind11.h&gt;namespace py = pybind11;class MathUtils &#123;public:    static int square(int x) &#123;        return x * x;    &#125;        static double pi;&#125;;double MathUtils::pi = 3.1415926535;PYBIND11_MODULE(example, m) &#123;    py::class_&lt;MathUtils&gt;(m, &quot;MathUtils&quot;)        .def_static(&quot;square&quot;, &amp;MathUtils::square, &quot;计算平方&quot;)        .def_readwrite_static(&quot;pi&quot;, &amp;MathUtils::pi, &quot;圆周率&quot;);&#125;
