4. 运算符重载
#include &lt;pybind11/pybind11.h&gt;#include &lt;pybind11/operators.h&gt;namespace py = pybind11;class Vector2 &#123;public:    float x, y;        Vector2(float x = 0, float y = 0) : x(x), y(y) &#123;&#125;        Vector2 operator+(const Vector2&amp; other) const &#123;        return Vector2(x + other.x, y + other.y);    &#125;        Vector2 operator*(float scalar) const &#123;        return Vector2(x * scalar, y * scalar);    &#125;        bool operator==(const Vector2&amp; other) const &#123;        return x == other.x &amp;&amp; y == other.y;    &#125;        std::string to_string() const &#123;        return &quot;(&quot; + std::to_string(x) + &quot;, &quot; + std::to_string(y) + &quot;)&quot;;    &#125;&#125;;PYBIND11_MODULE(example, m) &#123;    py::class_&lt;Vector2&gt;(m, &quot;Vector2&quot;)        .def(py::init&lt;float, float&gt;(), &quot;二维向量&quot;)        .def_readwrite(&quot;x&quot;, &amp;Vector2::x)        .def_readwrite(&quot;y&quot;, &amp;Vector2::y)        .def(&quot;__str__&quot;, &amp;Vector2::to_string)                // 运算符重载        .def(py::self + py::self)          // + 运算符        .def(py::self * float())           // * 运算符（向量 * 标量）        .def(float() * py::self)           // * 运算符（标量 * 向量）        .def(py::self == py::self);        // == 运算符&#125;
Python 使用：
import examplev1 = example.Vector2(1, 2)v2 = example.Vector2(3, 4)v3 = v1 + v2print(v3)  # 输出：(4.000000, 6.000000)v4 = v1 * 2print(v4)  # 输出：(2.000000, 4.000000)v5 = 3 * v2print(v5)  # 输出：(9.000000, 12.000000)print(v1 == v2)  # 输出：False
实际案例
案例 1：高性能数值计算
#include &lt;pybind11/pybind11.h&gt;#include &lt;pybind11/numpy.h&gt;#include &lt;vector&gt;namespace py = pybind11;// 计算数组的移动平均值py::array_t&lt;double&gt; moving_average(py::array_t&lt;double&gt; data, int window_size) &#123;    py::buffer_info buf = data.request();        if (buf.ndim != 1) &#123;        throw std::runtime_error(&quot;只支持一维数组&quot;);    &#125;        size_t n = buf.size;    if (window_size &gt; n) &#123;        throw std::runtime_error(&quot;窗口大小不能大于数组长度&quot;);    &#125;        auto result = py::array_t&lt;double&gt;(n - window_size + 1);    py::buffer_info res_buf = result.request();        double *data_ptr = static_cast&lt;double *&gt;(buf.ptr);    double *res_ptr = static_cast&lt;double *&gt;(res_buf.ptr);        // 计算初始窗口    double sum = 0;    for (int i = 0; i &lt; window_size; i++) &#123;        sum += data_ptr[i];    &#125;    res_ptr[0] = sum / window_size;        // 滑动窗口    for (size_t i = 1; i &lt;= n - window_size; i++) &#123;        sum = sum - data_ptr[i-1] + data_ptr[i + window_size - 1];        res_ptr[i] = sum / window_size;    &#125;        return result;&#125;PYBIND11_MODULE(performance, m) &#123;    m.def(&quot;moving_average&quot;, &amp;moving_average, &quot;计算移动平均值&quot;,          py::arg(&quot;data&quot;), py::arg(&quot;window_size&quot;));&#125;
案例 2：图像处理算法
#include &lt;pybind11/pybind11.h&gt;#include &lt;pybind11/numpy.h&gt;#include &lt;algorithm&gt;namespace py = pybind11;// 图像灰度化py::array_t&lt;uint8_t&gt; rgb_to_gray(py::array_t&lt;uint8_t&gt; image) &#123;    py::buffer_info buf = image.request();        if (buf.ndim != 3 || buf.shape[2] != 3) &#123;        throw std::runtime_error(&quot;输入必须是RGB图像&quot;);    &#125;        size_t height = buf.shape[0];    size_t width = buf.shape[1];        auto gray = py::array_t&lt;uint8_t&gt;(&#123;height, width&#125;);    py::buffer_info gray_buf = gray.request();        uint8_t *img_ptr = static_cast&lt;uint8_t *&gt;(buf.ptr);    uint8_t *gray_ptr = static_cast&lt;uint8_t *&gt;(gray_buf.ptr);        for (size_t i = 0; i &lt; height; i++) &#123;        for (size_t j = 0; j &lt; width; j++) &#123;            size_t idx = i * width * 3 + j * 3;            uint8_t r = img_ptr[idx];            uint8_t g = img_ptr[idx + 1];            uint8_t b = img_ptr[idx + 2];                        // 灰度化公式            gray_ptr[i * width + j] = static_cast&lt;uint8_t&gt;(                0.299 * r + 0.587 * g + 0.114 * b            );        &#125;    &#125;        return gray;&#125;PYBIND11_MODULE(imageprocessing, m) &#123;    m.def(&quot;rgb_to_gray&quot;, &amp;rgb_to_gray, &quot;RGB图像转灰度图像&quot;);&#125;
案例 3：数据结构包装
#include &lt;pybind11/pybind11.h&gt;#include &lt;pybind11/stl.h&gt;#include &lt;queue&gt;#include &lt;stack&gt;#include &lt;unordered_map&gt;namespace py = pybind11;// 栈数据结构template &lt;typename T&gt;class Stack &#123;private:    std::stack&lt;T&gt; stack_;    public:    void push(const T&amp; value) &#123;        stack_.push(value);    &#125;        T pop() &#123;        if (stack_.empty()) &#123;            throw std::runtime_error(&quot;栈为空&quot;);        &#125;        T top = stack_.top();        stack_.pop();        return top;    &#125;        T top() const &#123;        if (stack_.empty()) &#123;            throw std::runtime_error(&quot;栈为空&quot;);        &#125;        return stack_.top();    &#125;        bool empty() const &#123;        return stack_.empty();    &#125;        size_t size() const &#123;        return stack_.size();    &#125;&#125;;// 队列数据结构template &lt;typename T&gt;class Queue &#123;private:    std::queue&lt;T&gt; queue_;    public:    void enqueue(const T&amp; value) &#123;        queue_.push(value);    &#125;        T dequeue() &#123;        if (queue_.empty()) &#123;            throw std::runtime_error(&quot;队列为空&quot;);        &#125;        T front = queue_.front();        queue_.pop();        return front;    &#125;        T front() const &#123;        if (queue_.empty()) &#123;            throw std::runtime_error(&quot;队列为空&quot;);        &#125;        return queue_.front();    &#125;        bool empty() const &#123;        return queue_.empty();    &#125;        size_t size() const &#123;        return queue_.size();    &#125;&#125;;PYBIND11_MODULE(datastructures, m) &#123;    // 绑定栈    py::class_&lt;Stack&lt;int&gt;&gt;(m, &quot;IntStack&quot;)        .def(py::init&lt;&gt;())        .def(&quot;push&quot;, &amp;Stack&lt;int&gt;::push)        .def(&quot;pop&quot;, &amp;Stack&lt;int&gt;::pop)        .def(&quot;top&quot;, &amp;Stack&lt;int&gt;::top)        .def(&quot;empty&quot;, &amp;Stack&lt;int&gt;::empty)        .def(&quot;size&quot;, &amp;Stack&lt;int&gt;::size);        // 绑定队列    py::class_&lt;Queue&lt;std::string&gt;&gt;(m, &quot;StringQueue&quot;)        .def(py::init&lt;&gt;())        .def(&quot;enqueue&quot;, &amp;Queue&lt;std::string&gt;::enqueue)        .def(&quot;dequeue&quot;, &amp;Queue&lt;std::string&gt;::dequeue)        .def(&quot;front&quot;, &amp;Queue&lt;std::string&gt;::front)        .def(&quot;empty&quot;, &amp;Queue&lt;std::string&gt;::empty)        .def(&quot;size&quot;, &amp;Queue&lt;std::string&gt;::size);&#125;
