from setuptools import setup, Extensionfrom pybind11.setup_helpers import Pybind11Extension, build_extfrom pybind11 import get_cmake_dirimport glob# 并行编译def parallel_compile_test_parallel_compile(self, sources, output_dir=None, macros=None,                                          include_dirs=None, debug=0, extra_preargs=None,                                          extra_postargs=None, depends=None):    import multiprocessing    from setuptools._distutils.ccompiler import CCompiler    from setuptools._distutils import log    # 复制原始实现    original_compile = CCompiler._compile    def _compile_single(source):        return original_compile(self, source, output_dir, macros, include_dirs,                               debug, extra_preargs, extra_postargs, depends)    # 使用多进程编译    if not sources:        return    num_jobs = multiprocessing.cpu_count()    log.info(f&quot;Using &#123;num_jobs&#125; parallel jobs for compilation&quot;)        import multiprocessing.pool    pool = multiprocessing.pool.ThreadPool(num_jobs)    try:        pool.map(_compile_single, sources)    finally:        pool.close()        pool.join()# 应用并行编译补丁import setuptools._distutils.ccompilersetuptools._distutils.ccompiler.CCompiler._compile = parallel_compile_test_parallel_compile# 扩展模块ext_modules = [    Pybind11Extension(        &quot;example.core&quot;,        sorted(glob.glob(&quot;src/core/*.cpp&quot;)),        include_dirs=[&quot;src/include&quot;],        libraries=[&quot;my_custom_lib&quot;],        library_dirs=[&quot;lib&quot;],        extra_compile_args=[&quot;-O3&quot;, &quot;-std=c++17&quot;, &quot;-Wall&quot;],        extra_link_args=[&quot;-Wl,-rpath,$ORIGIN/lib&quot;],    ),    Pybind11Extension(        &quot;example.utils&quot;,        sorted(glob.glob(&quot;src/utils/*.cpp&quot;)),        include_dirs=[&quot;src/include&quot;],        extra_compile_args=[&quot;-O3&quot;, &quot;-std=c++17&quot;],    ),]setup(    name=&quot;example&quot;,    version=&quot;1.0.0&quot;,    author=&quot;Your Name&quot;,    author_email=&quot;your.email@example.com&quot;,    description=&quot;pybind11示例项目&quot;,    long_description=open(&quot;README.md&quot;).read(),    long_description_content_type=&quot;text/markdown&quot;,    url=&quot;https://github.com/yourusername/example&quot;,    ext_modules=ext_modules,    cmdclass=&#123;&quot;build_ext&quot;: build_ext&#125;,    zip_safe=False,    packages=[&quot;example&quot;],    package_dir=&#123;&quot;example&quot;: &quot;python/example&quot;&#125;,    package_data=&#123;&quot;example&quot;: [&quot;py.typed&quot;]&#125;,    classifiers=[        &quot;Programming Language :: C++&quot;,        &quot;Programming Language :: Python :: 3&quot;,        &quot;License :: OSI Approved :: MIT License&quot;,        &quot;Operating System :: OS Independent&quot;,    ],    python_requires=&quot;&gt;=3.8&quot;,    install_requires=[&quot;numpy&gt;=1.21&quot;],    extras_require=&#123;        &quot;dev&quot;: [            &quot;pytest&gt;=6.0&quot;,            &quot;pytest-cov&gt;=2.0&quot;,            &quot;black&gt;=21.0&quot;,            &quot;flake8&gt;=3.9&quot;,        ]    &#125;,)
返回值策略
1. 策略概述



策略
描述




return_value_policy::take_ownership
接管现有对象的所有权，Python 会在引用计数为零时删除对象


return_value_policy::copy
创建返回对象的副本，由 Python 拥有


return_value_policy::move
使用 std::move 转移内容到新实例


return_value_policy::reference
引用现有对象但不接管所有权


return_value_policy::reference_internal
引用内部对象，确保父对象存活


return_value_policy::automatic
自动选择策略（默认）


return_value_policy::automatic_reference
类似 automatic，但对指针使用 reference 策略



2. 使用示例
#include &lt;pybind11/pybind11.h&gt;#include &lt;memory&gt;namespace py = pybind11;class MyClass &#123;public:    MyClass(int value) : value(value) &#123;&#125;    int get_value() const &#123; return value; &#125;    private:    int value;&#125;;// 静态对象 - 使用reference策略MyClass static_instance(42);MyClass&amp; get_static_instance() &#123;    return static_instance;&#125;// 动态分配 - 使用take_ownership策略MyClass* create_instance(int value) &#123;    return new MyClass(value);&#125;// 内部对象 - 使用reference_internal策略class Container &#123;public:    Container() : internal(100) &#123;&#125;    MyClass&amp; get_internal() &#123; return internal; &#125;    private:    MyClass internal;&#125;;PYBIND11_MODULE(example, m) &#123;    py::class_&lt;MyClass&gt;(m, &quot;MyClass&quot;)        .def(py::init&lt;int&gt;())        .def(&quot;get_value&quot;, &amp;MyClass::get_value);        py::class_&lt;Container&gt;(m, &quot;Container&quot;)        .def(py::init&lt;&gt;())        .def(&quot;get_internal&quot;, &amp;Container::get_internal,              py::return_value_policy::reference_internal);        // 静态对象 - 使用reference策略    m.def(&quot;get_static_instance&quot;, &amp;get_static_instance,           py::return_value_policy::reference);        // 动态对象 - 使用take_ownership策略    m.def(&quot;create_instance&quot;, &amp;create_instance,           py::return_value_policy::take_ownership);        // 智能指针 - 自动处理    m.def(&quot;create_shared&quot;, []() &#123;        return std::make_shared&lt;MyClass&gt;(200);    &#125;);&#125;
3. 常见错误及解决方案
错误示例：
// 危险！静态对象使用默认策略会导致double freem.def(&quot;get_static&quot;, &amp;get_static_instance);  // 错误
正确做法：
// 正确：使用reference策略m.def(&quot;get_static&quot;, &amp;get_static_instance, py::return_value_policy::reference);
智能指针
1. py::smart_holder（推荐）
pybind11 v3 引入的智能持有者，支持双向转换：
#include &lt;pybind11/pybind11.h&gt;#include &lt;memory&gt;namespace py = pybind11;class MyClass &#123;public:    MyClass(int value) : value(value) &#123;&#125;    int get_value() const &#123; return value; &#125;    private:    int value;&#125;;PYBIND11_MODULE(example, m) &#123;    // 使用py::smart_holder    py::class_&lt;MyClass, py::smart_holder&gt;(m, &quot;MyClass&quot;)        .def(py::init&lt;int&gt;())        .def(&quot;get_value&quot;, &amp;MyClass::get_value);        // 支持unique_ptr和shared_ptr双向转换    m.def(&quot;create_unique&quot;, []() &#123;        return std::make_unique&lt;MyClass&gt;(10);    &#125;);        m.def(&quot;create_shared&quot;, []() &#123;        return std::make_shared&lt;MyClass&gt;(20);    &#125;);        m.def(&quot;process_unique&quot;, [](std::unique_ptr&lt;MyClass&gt; ptr) &#123;        return ptr-&gt;get_value() * 2;    &#125;);        m.def(&quot;process_shared&quot;, [](std::shared_ptr&lt;MyClass&gt; ptr) &#123;        return ptr-&gt;get_value() * 3;    &#125;);&#125;
Python 使用：
import example# 从Python传递到C++obj = example.MyClass(5)print(example.process_unique(obj))  # 输出：10（obj不再有效）obj2 = example.MyClass(6)print(example.process_shared(obj2))  # 输出：18（obj2仍然有效）# 从C++返回unique_obj = example.create_unique()shared_obj = example.create_shared()
2. std::unique_ptr
#include &lt;pybind11/pybind11.h&gt;#include &lt;memory&gt;namespace py = pybind11;class MyClass &#123;public:    MyClass(int value) : value(value) &#123;&#125;    int get_value() const &#123; return value; &#125;    private:    int value;&#125;;PYBIND11_MODULE(example, m) &#123;    py::class_&lt;MyClass&gt;(m, &quot;MyClass&quot;)        .def(py::init&lt;int&gt;())        .def(&quot;get_value&quot;, &amp;MyClass::get_value);        // 返回unique_ptr    m.def(&quot;create&quot;, []() &#123;        return std::make_unique&lt;MyClass&gt;(10);    &#125;);        // 接受unique_ptr（会转移所有权）    m.def(&quot;take_ownership&quot;, [](std::unique_ptr&lt;MyClass&gt; ptr) &#123;        return ptr-&gt;get_value();    &#125;);&#125;
3. std::shared_ptr
#include &lt;pybind11/pybind11.h&gt;#include &lt;memory&gt;namespace py = pybind11;class MyClass &#123;public:    MyClass(int value) : value(value) &#123;&#125;    int get_value() const &#123; return value; &#125;    private:    int value;&#125;;PYBIND11_MODULE(example, m) &#123;    // 使用shared_ptr作为持有者    py::class_&lt;MyClass, std::shared_ptr&lt;MyClass&gt;&gt;(m, &quot;MyClass&quot;)        .def(py::init&lt;int&gt;())        .def(&quot;get_value&quot;, &amp;MyClass::get_value);        m.def(&quot;create&quot;, []() &#123;        return std::make_shared&lt;MyClass&gt;(10);    &#125;);        m.def(&quot;share&quot;, [](std::shared_ptr&lt;MyClass&gt; ptr) &#123;        return ptr;  // 共享所有权    &#125;);&#125;
4. 自定义智能指针
#include &lt;pybind11/pybind11.h&gt;#include &lt;memory&gt;namespace py = pybind11;// 自定义智能指针template &lt;typename T&gt;class MySmartPtr &#123;public:    MySmartPtr(T* ptr) : ptr(ptr) &#123;&#125;    ~MySmartPtr() &#123; delete ptr; &#125;        T* get() const &#123; return ptr; &#125;    T&amp; operator*() const &#123; return *ptr; &#125;    T* operator-&gt;() const &#123; return ptr; &#125;    private:    T* ptr;&#125;;class MyClass &#123;public:    MyClass(int value) : value(value) &#123;&#125;    int get_value() const &#123; return value; &#125;    private:    int value;&#125;;// 声明自定义持有者类型PYBIND11_DECLARE_HOLDER_TYPE(T, MySmartPtr&lt;T&gt;)// 特化holder_helper（如果需要）namespace pybind11 &#123; namespace detail &#123;    template &lt;typename T&gt;    struct holder_helper&lt;MySmartPtr&lt;T&gt;&gt; &#123;        static const T* get(const MySmartPtr&lt;T&gt;&amp; p) &#123; return p.get(); &#125;    &#125;;&#125;&#125;PYBIND11_MODULE(example, m) &#123;    py::class_&lt;MyClass, MySmartPtr&lt;MyClass&gt;&gt;(m, &quot;MyClass&quot;)        .def(py::init&lt;int&gt;())        .def(&quot;get_value&quot;, &amp;MyClass::get_value);        m.def(&quot;create&quot;, []() &#123;        return MySmartPtr&lt;MyClass&gt;(new MyClass(10));    &#125;);&#125;
