import exampleimport datetime# 时间点转换now = datetime.datetime.now()later = example.add_seconds(now, 3600)  # 加1小时print(later)# 时间段转换start = datetime.datetime(2023, 1, 1)end = datetime.datetime(2023, 1, 2)diff = example.time_difference(start, end)print(diff.total_seconds())  # 输出：86400.0
嵌入 Python 解释器
1. 基础嵌入
CMake 配置：
cmake_minimum_required(VERSION 3.15...3.27)project(embedded_example LANGUAGES CXX)find_package(pybind11 CONFIG REQUIRED)add_executable(embedded_example main.cpp)target_link_libraries(embedded_example PRIVATE pybind11::embed)
C++ 代码：
#include &lt;pybind11/embed.h&gt;#include &lt;iostream&gt;namespace py = pybind11;int main() &#123;    // 启动Python解释器    py::scoped_interpreter guard&#123;&#125;;        try &#123;        // 执行Python代码        py::print(&quot;Hello from embedded Python!&quot;);                // 使用Python标准库        py::module math = py::module::import(&quot;math&quot;);        double result = math.attr(&quot;sqrt&quot;)(25.0).cast&lt;double&gt;();        std::cout &lt;&lt; &quot;sqrt(25) = &quot; &lt;&lt; result &lt;&lt; std::endl;                // 执行多行Python代码        py::exec(R&quot;(            import sys            print(f&quot;Python version: &#123;sys.version&#125;&quot;)                        def add(a, b):                return a + b                        result = add(3, 4)            print(f&quot;3 + 4 = &#123;result&#125;&quot;)        )&quot;);                // 调用Python函数        auto add = py::eval(&quot;add&quot;).cast&lt;py::function&gt;();        int sum_result = add(5, 6).cast&lt;int&gt;();        std::cout &lt;&lt; &quot;5 + 6 = &quot; &lt;&lt; sum_result &lt;&lt; std::endl;            &#125; catch (const py::error_already_set&amp; e) &#123;        std::cerr &lt;&lt; &quot;Python error: &quot; &lt;&lt; e.what() &lt;&lt; std::endl;        return 1;    &#125;        // 解释器会在guard析构时自动关闭    return 0;&#125;
2. 嵌入模块
#include &lt;pybind11/embed.h&gt;#include &lt;pybind11/pybind11.h&gt;namespace py = pybind11;// 嵌入的C++模块PYBIND11_EMBEDDED_MODULE(embedded_module, m) &#123;    m.def(&quot;multiply&quot;, [](int a, int b) &#123;        return a * b;    &#125;);        m.def(&quot;greet&quot;, [](const std::string&amp; name) &#123;        return &quot;Hello, &quot; + name + &quot;!&quot;;    &#125;);&#125;int main() &#123;    py::scoped_interpreter guard&#123;&#125;;        try &#123;        // 导入嵌入的模块        py::module em = py::module::import(&quot;embedded_module&quot;);                // 调用嵌入模块的函数        int product = em.attr(&quot;multiply&quot;)(3, 4).cast&lt;int&gt;();        std::cout &lt;&lt; &quot;3 * 4 = &quot; &lt;&lt; product &lt;&lt; std::endl;                std::string greeting = em.attr(&quot;greet&quot;)(&quot;World&quot;).cast&lt;std::string&gt;();        std::cout &lt;&lt; greeting &lt;&lt; std::endl;                // 在Python中使用嵌入模块        py::exec(R&quot;(            result = embedded_module.multiply(5, 6)            print(f&quot;5 * 6 = &#123;result&#125;&quot;)                        message = embedded_module.greet(&quot;Python&quot;)            print(message)        )&quot;);            &#125; catch (const py::error_already_set&amp; e) &#123;        std::cerr &lt;&lt; &quot;Error: &quot; &lt;&lt; e.what() &lt;&lt; std::endl;        return 1;    &#125;        return 0;&#125;
3. 导入本地 Python 文件
#include &lt;pybind11/embed.h&gt;#include &lt;iostream&gt;#include &lt;filesystem&gt;namespace py = pybind11;namespace fs = std::filesystem;int main() &#123;    py::scoped_interpreter guard&#123;&#125;;        try &#123;        // 添加当前目录到Python路径        py::module sys = py::module::import(&quot;sys&quot;);        sys.attr(&quot;path&quot;).cast&lt;py::list&gt;().append(fs::current_path().string());                // 导入本地Python模块        py::module my_module = py::module::import(&quot;my_python_module&quot;);                // 调用Python函数        int result = my_module.attr(&quot;my_function&quot;)(10, 20).cast&lt;int&gt;();        std::cout &lt;&lt; &quot;Result from Python: &quot; &lt;&lt; result &lt;&lt; std::endl;            &#125; catch (const py::error_already_set&amp; e) &#123;        std::cerr &lt;&lt; &quot;Error: &quot; &lt;&lt; e.what() &lt;&lt; std::endl;        return 1;    &#125;        return 0;&#125;
多线程与 GIL 管理
1. GIL 释放与获取
#include &lt;pybind11/pybind11.h&gt;#include &lt;pybind11/functional.h&gt;#include &lt;thread&gt;#include &lt;chrono&gt;#include &lt;iostream&gt;namespace py = pybind11;// 长时间运行的C++函数 - 应该释放GILvoid long_running_task() &#123;    // 释放GIL    py::gil_scoped_release release;        std::cout &lt;&lt; &quot;Long running task started (thread: &quot;               &lt;&lt; std::this_thread::get_id() &lt;&lt; &quot;)&quot; &lt;&lt; std::endl;        // 模拟长时间计算    for (int i = 0; i &lt; 5; ++i) &#123;        std::this_thread::sleep_for(std::chrono::seconds(1));        std::cout &lt;&lt; &quot;Task progress: &quot; &lt;&lt; (i + 1) &lt;&lt; &quot;/5&quot; &lt;&lt; std::endl;    &#125;        std::cout &lt;&lt; &quot;Long running task completed&quot; &lt;&lt; std::endl;        // GIL会在release析构时自动重新获取&#125;// 使用call_guard自动管理GILvoid task_with_guard() &#123;    std::cout &lt;&lt; &quot;Task with guard started&quot; &lt;&lt; std::endl;    std::this_thread::sleep_for(std::chrono::seconds(3));    std::cout &lt;&lt; &quot;Task with guard completed&quot; &lt;&lt; std::endl;&#125;// 多线程函数void run_in_threads() &#123;    std::thread t1(long_running_task);    std::thread t2(long_running_task);        t1.join();    t2.join();&#125;PYBIND11_MODULE(example, m) &#123;    // 直接释放GIL    m.def(&quot;long_running_task&quot;, &amp;long_running_task);        // 使用call_guard释放GIL    m.def(&quot;task_with_guard&quot;, &amp;task_with_guard,           py::call_guard&lt;py::gil_scoped_release&gt;());        // 多线程示例    m.def(&quot;run_in_threads&quot;, &amp;run_in_threads);        // 线程安全的函数调用    m.def(&quot;thread_safe_function&quot;, [](int value) &#123;        // 在关键部分获取GIL        py::gil_scoped_acquire acquire;        py::print(&quot;Thread-safe function called with value:&quot;, value);    &#125;);&#125;
Python 使用：
import exampleimport threadingimport time# 测试长时间任务print(&quot;Testing long running task...&quot;)thread1 = threading.Thread(target=example.long_running_task)thread2 = threading.Thread(target=example.long_running_task)thread1.start()thread2.start()# 主线程可以继续执行其他任务for i in range(3):    print(f&quot;Main thread working... &#123;i&#125;&quot;)    time.sleep(1)thread1.join()thread2.join()# 测试带guard的任务print(&quot;Testing task with guard...&quot;)example.task_with_guard()# 测试多线程函数print(&quot;Testing run in threads...&quot;)example.run_in_threads()
2. 线程局部存储
#include &lt;pybind11/pybind11.h&gt;#include &lt;thread&gt;#include &lt;mutex&gt;#include &lt;iostream&gt;namespace py = pybind11;// 线程局部存储thread_local int thread_counter = 0;std::mutex cout_mutex;void thread_function(int id) &#123;    // 释放GIL    py::gil_scoped_release release;        for (int i = 0; i &lt; 5; ++i) &#123;        thread_counter++;                // 安全的输出        std::lock_guard&lt;std::mutex&gt; lock(cout_mutex);        std::cout &lt;&lt; &quot;Thread &quot; &lt;&lt; id &lt;&lt; &quot;: counter = &quot; &lt;&lt; thread_counter &lt;&lt; std::endl;                std::this_thread::sleep_for(std::chrono::milliseconds(100));    &#125;&#125;void start_threads() &#123;    std::thread t1(thread_function, 1);    std::thread t2(thread_function, 2);        t1.join();    t2.join();&#125;PYBIND11_MODULE(example, m) &#123;    m.def(&quot;start_threads&quot;, &amp;start_threads);&#125;
3. 异步任务
#include &lt;pybind11/pybind11.h&gt;#include &lt;pybind11/functional.h&gt;#include &lt;thread&gt;#include &lt;future&gt;#include &lt;iostream&gt;namespace py = pybind11;// 异步执行函数std::future&lt;int&gt; async_task(int x, int y) &#123;    return std::async(std::launch::async, [x, y]() &#123;        // 释放GIL        py::gil_scoped_release release;                std::cout &lt;&lt; &quot;Async task started (thread: &quot;                   &lt;&lt; std::this_thread::get_id() &lt;&lt; &quot;)&quot; &lt;&lt; std::endl;                // 模拟计算        std::this_thread::sleep_for(std::chrono::seconds(2));        return x + y;    &#125;);&#125;// 异步回调void async_callback(py::function callback, int x, int y) &#123;    std::thread([callback, x, y]() &#123;        // 释放GIL        py::gil_scoped_release release;                std::cout &lt;&lt; &quot;Callback task started&quot; &lt;&lt; std::endl;        std::this_thread::sleep_for(std::chrono::seconds(2));                // 计算结果        int result = x * y;                // 调用回调函数时重新获取GIL        py::gil_scoped_acquire acquire;        callback(result);    &#125;).detach();&#125;PYBIND11_MODULE(example, m) &#123;    m.def(&quot;async_task&quot;, [](int x, int y) &#123;        auto future = async_task(x, y);        return future.get();    &#125;);        m.def(&quot;async_callback&quot;, &amp;async_callback);&#125;
Python 使用：
import exampleimport time# 测试异步任务print(&quot;Testing async task...&quot;)result = example.async_task(3, 4)print(f&quot;Async result: &#123;result&#125;&quot;)# 测试异步回调def on_complete(result):    print(f&quot;Callback received: &#123;result&#125;&quot;)print(&quot;Testing async callback...&quot;)example.async_callback(on_complete, 5, 6)print(&quot;Callback task started (will complete in 2 seconds)&quot;)# 等待回调完成time.sleep(3)
子解释器
1. 基础子解释器使用
#include &lt;pybind11/embed.h&gt;#include &lt;pybind11/subinterpreter.h&gt;#include &lt;iostream&gt;namespace py = pybind11;int main() &#123;    // 初始化主解释器    py::scoped_interpreter main_guard&#123;&#125;;        try &#123;        std::cout &lt;&lt; &quot;Main interpreter started&quot; &lt;&lt; std::endl;                // 创建子解释器        py::subinterpreter sub = py::subinterpreter::create();                &#123;            // 激活子解释器            py::subinterpreter_scoped_activate activate(sub);                        std::cout &lt;&lt; &quot;Sub-interpreter activated&quot; &lt;&lt; std::endl;                        // 在子解释器中执行代码            py::exec(R&quot;(                import sys                print(f&quot;Sub-interpreter Python version: &#123;sys.version&#125;&quot;)                counter = 42            )&quot;);                        // 获取变量值            int counter = py::eval(&quot;counter&quot;).cast&lt;int&gt;();            std::cout &lt;&lt; &quot;Sub-interpreter counter: &quot; &lt;&lt; counter &lt;&lt; std::endl;        &#125;                std::cout &lt;&lt; &quot;Sub-interpreter deactivated&quot; &lt;&lt; std::endl;                // 主解释器不受影响        py::exec(&quot;print(&#x27;Back to main interpreter&#x27;)&quot;);            &#125; catch (const py::error_already_set&amp; e) &#123;        std::cerr &lt;&lt; &quot;Error: &quot; &lt;&lt; e.what() &lt;&lt; std::endl;        return 1;    &#125;        return 0;&#125;
2. 多线程子解释器
#include &lt;pybind11/embed.h&gt;#include &lt;pybind11/subinterpreter.h&gt;#include &lt;thread&gt;#include &lt;iostream&gt;namespace py = pybind11;void run_in_subinterpreter(py::subinterpreter sub, int id) &#123;    try &#123;        // 激活子解释器        py::subinterpreter_scoped_activate activate(sub);                std::cout &lt;&lt; &quot;Thread &quot; &lt;&lt; id &lt;&lt; &quot; started in sub-interpreter&quot; &lt;&lt; std::endl;                // 执行Python代码        py::exec(R&quot;(            import time            import threading                        def task():                for i in range(3):                    print(f&quot;Task &#123;id&#125; iteration &#123;i&#125; (thread: &#123;threading.get_ident()&#125;)&quot;)                    time.sleep(1)                        task()        )&quot;);                std::cout &lt;&lt; &quot;Thread &quot; &lt;&lt; id &lt;&lt; &quot; completed&quot; &lt;&lt; std::endl;            &#125; catch (const py::error_already_set&amp; e) &#123;        std::cerr &lt;&lt; &quot;Thread &quot; &lt;&lt; id &lt;&lt; &quot; error: &quot; &lt;&lt; e.what() &lt;&lt; std::endl;    &#125;&#125;int main() &#123;    py::scoped_interpreter main_guard&#123;&#125;;        try &#123;        // 创建多个子解释器        py::subinterpreter sub1 = py::subinterpreter::create();        py::subinterpreter sub2 = py::subinterpreter::create();                // 在不同线程中运行子解释器        std::thread t1(run_in_subinterpreter, std::move(sub1), 1);        std::thread t2(run_in_subinterpreter, std::move(sub2), 2);                t1.join();        t2.join();            &#125; catch (const py::error_already_set&amp; e) &#123;        std::cerr &lt;&lt; &quot;Main error: &quot; &lt;&lt; e.what() &lt;&lt; std::endl;        return 1;    &#125;        return 0;&#125;
3. 子解释器安全最佳实践
#include &lt;pybind11/embed.h&gt;#include &lt;pybind11/subinterpreter.h&gt;#include &lt;thread&gt;#include &lt;mutex&gt;#include &lt;iostream&gt;namespace py = pybind11;// 线程安全的子解释器管理器class SubinterpreterManager &#123;public:    SubinterpreterManager() : main_guard_&#123;&#125; &#123;&#125;        py::subinterpreter create_subinterpreter() &#123;        std::lock_guard&lt;std::mutex&gt; lock(mutex_);        return py::subinterpreter::create();    &#125;    private:    py::scoped_interpreter main_guard_;    std::mutex mutex_;&#125;;// 安全的任务函数void safe_task(py::subinterpreter sub, const std::string&amp; name) &#123;    try &#123;        // 子解释器作用域        py::subinterpreter_scoped_activate activate(sub);                // 任务逻辑        py::exec(R&quot;(            import time                        def safe_operation(name):                result = []                for i in range(5):                    result.append(f&quot;&#123;name&#125;: &#123;i&#125;&quot;)                    time.sleep(0.1)                return result                        result = safe_operation(name)            print(f&quot;Task &#123;name&#125; completed with &#123;len(result)&#125; items&quot;)        )&quot;);            &#125; catch (const py::error_already_set&amp; e) &#123;        std::cerr &lt;&lt; &quot;Task &quot; &lt;&lt; name &lt;&lt; &quot; failed: &quot; &lt;&lt; e.what() &lt;&lt; std::endl;    &#125;&#125;int main() &#123;    SubinterpreterManager manager;        // 创建并运行多个子解释器任务    std::vector&lt;std::thread&gt; threads;    for (int i = 0; i &lt; 3; ++i) &#123;        py::subinterpreter sub = manager.create_subinterpreter();        threads.emplace_back(safe_task, std::move(sub), &quot;task_&quot; + std::to_string(i));    &#125;        // 等待所有线程完成    for (auto&amp; t : threads) &#123;        t.join();    &#125;        return 0;&#125;
自定义类型转换
1. 基础自定义转换器
#include &lt;pybind11/pybind11.h&gt;#include &lt;string&gt;#include &lt;sstream&gt;namespace py = pybind11;// 自定义类型struct Point &#123;    int x;    int y;        Point(int x = 0, int y = 0) : x(x), y(y) &#123;&#125;        std::string to_string() const &#123;        std::stringstream ss;        ss &lt;&lt; &quot;(&quot; &lt;&lt; x &lt;&lt; &quot;, &quot; &lt;&lt; y &lt;&lt; &quot;)&quot;;        return ss.str();    &#125;&#125;;// 自定义类型转换器namespace pybind11 &#123; namespace detail &#123;    template &lt;&gt; struct type_caster&lt;Point&gt; &#123;    public:        PYBIND11_TYPE_CASTER(Point, _(&quot;Point&quot;));                // 从Python对象转换到C++        bool load(handle src, bool) &#123;            if (!src.is_dict()) return false;                        py::dict obj = src.cast&lt;py::dict&gt;();            value.x = obj[&quot;x&quot;].cast&lt;int&gt;();            value.y = obj[&quot;y&quot;].cast&lt;int&gt;();            return true;        &#125;                // 从C++转换到Python对象        static handle cast(const Point&amp; p, return_value_policy /* policy */, handle /* parent */) &#123;            py::dict obj;            obj[&quot;x&quot;] = p.x;            obj[&quot;y&quot;] = p.y;            return obj.release();        &#125;    &#125;;&#125;&#125;PYBIND11_MODULE(example, m) &#123;    // 使用自定义转换器    m.def(&quot;add_points&quot;, [](const Point&amp; a, const Point&amp; b) &#123;        return Point(a.x + b.x, a.y + b.y);    &#125;);        m.def(&quot;print_point&quot;, [](const Point&amp; p) &#123;        py::print(p.to_string());    &#125;);&#125;
Python 使用：
import example# 创建Point对象（通过字典）p1 = &#123;&quot;x&quot;: 1, &quot;y&quot;: 2&#125;p2 = &#123;&quot;x&quot;: 3, &quot;y&quot;: 4&#125;# 使用自定义转换result = example.add_points(p1, p2)print(result)  # 输出：&#123;&#x27;x&#x27;: 4, &#x27;y&#x27;: 6&#125;example.print_point(result)  # 输出：(4, 6)
2. 支持多种 Python 类型的转换器
#include &lt;pybind11/pybind11.h&gt;#include &lt;pybind11/stl.h&gt;#include &lt;string&gt;#include &lt;vector&gt;namespace py = pybind11;struct Rectangle &#123;    int x, y, width, height;        Rectangle(int x=0, int y=0, int w=0, int h=0)         : x(x), y(y), width(w), height(h) &#123;&#125;        int area() const &#123; return width * height; &#125;&#125;;namespace pybind11 &#123; namespace detail &#123;    template &lt;&gt; struct type_caster&lt;Rectangle&gt; &#123;    public:        PYBIND11_TYPE_CASTER(Rectangle, _(&quot;Rectangle&quot;));                bool load(handle src, bool) &#123;            // 支持字典            if (src.is_dict()) &#123;                py::dict obj = src.cast&lt;py::dict&gt;();                value.x = obj[&quot;x&quot;].cast&lt;int&gt;();                value.y = obj[&quot;y&quot;].cast&lt;int&gt;();                value.width = obj[&quot;width&quot;].cast&lt;int&gt;();                value.height = obj[&quot;height&quot;].cast&lt;int&gt;();                return true;            &#125;            // 支持元组            else if (src.is_tuple()) &#123;                py::tuple obj = src.cast&lt;py::tuple&gt;();                if (obj.size() != 4) return false;                value.x = obj[0].cast&lt;int&gt;();                value.y = obj[1].cast&lt;int&gt;();                value.width = obj[2].cast&lt;int&gt;();                value.height = obj[3].cast&lt;int&gt;();                return true;            &#125;            // 支持列表            else if (src.is_list()) &#123;                py::list obj = src.cast&lt;py::list&gt;();                if (obj.size() != 4) return false;                value.x = obj[0].cast&lt;int&gt;();                value.y = obj[1].cast&lt;int&gt;();                value.width = obj[2].cast&lt;int&gt;();                value.height = obj[3].cast&lt;int&gt;();                return true;            &#125;                        return false;        &#125;                static handle cast(const Rectangle&amp; r, return_value_policy /* policy */, handle /* parent */) &#123;            py::dict obj;            obj[&quot;x&quot;] = r.x;            obj[&quot;y&quot;] = r.y;            obj[&quot;width&quot;] = r.width;            obj[&quot;height&quot;] = r.height;            obj[&quot;area&quot;] = r.area();            return obj.release();        &#125;    &#125;;&#125;&#125;PYBIND11_MODULE(example, m) &#123;    m.def(&quot;rectangle_area&quot;, [](const Rectangle&amp; r) &#123;        return r.area();    &#125;);        m.def(&quot;expand_rectangle&quot;, [](const Rectangle&amp; r, int amount) &#123;        return Rectangle(            r.x - amount,            r.y - amount,            r.width + 2 * amount,            r.height + 2 * amount        );    &#125;);&#125;
