3. 继承类型的转换器
#include &lt;pybind11/pybind11.h&gt;#include &lt;string&gt;#include &lt;memory&gt;namespace py = pybind11;// 基类struct Shape &#123;    virtual ~Shape() = default;    virtual std::string name() const = 0;    virtual double area() const = 0;&#125;;// 派生类struct Circle : public Shape &#123;    double radius;        Circle(double r) : radius(r) &#123;&#125;        std::string name() const override &#123; return &quot;Circle&quot;; &#125;    double area() const override &#123; return 3.14159 * radius * radius; &#125;&#125;;struct Square : public Shape &#123;    double side;        Square(double s) : side(s) &#123;&#125;        std::string name() const override &#123; return &quot;Square&quot;; &#125;    double area() const override &#123; return side * side; &#125;&#125;;// 基类转换器namespace pybind11 &#123; namespace detail &#123;    template &lt;&gt; struct type_caster&lt;std::shared_ptr&lt;Shape&gt;&gt; &#123;    public:        PYBIND11_TYPE_CASTER(std::shared_ptr&lt;Shape&gt;, _(&quot;Shape&quot;));                bool load(handle src, bool) &#123;            if (!src.is_dict()) return false;                        py::dict obj = src.cast&lt;py::dict&gt;();            std::string type = obj[&quot;type&quot;].cast&lt;std::string&gt;();                        if (type == &quot;circle&quot;) &#123;                double radius = obj[&quot;radius&quot;].cast&lt;double&gt;();                value = std::make_shared&lt;Circle&gt;(radius);            &#125; else if (type == &quot;square&quot;) &#123;                double side = obj[&quot;side&quot;].cast&lt;double&gt;();                value = std::make_shared&lt;Square&gt;(side);            &#125; else &#123;                return false;            &#125;                        return true;        &#125;                static handle cast(const std::shared_ptr&lt;Shape&gt;&amp; shape,                           return_value_policy /* policy */, handle /* parent */) &#123;            py::dict obj;                        if (auto circle = std::dynamic_pointer_cast&lt;Circle&gt;(shape)) &#123;                obj[&quot;type&quot;] = &quot;circle&quot;;                obj[&quot;radius&quot;] = circle-&gt;radius;            &#125; else if (auto square = std::dynamic_pointer_cast&lt;Square&gt;(shape)) &#123;                obj[&quot;type&quot;] = &quot;square&quot;;                obj[&quot;side&quot;] = square-&gt;side;            &#125;                        obj[&quot;name&quot;] = shape-&gt;name();            obj[&quot;area&quot;] = shape-&gt;area();                        return obj.release();        &#125;    &#125;;&#125;&#125;PYBIND11_MODULE(example, m) &#123;    m.def(&quot;shape_info&quot;, [](const std::shared_ptr&lt;Shape&gt;&amp; shape) &#123;        return py::make_tuple(shape-&gt;name(), shape-&gt;area());    &#125;);        m.def(&quot;create_shape&quot;, [](const std::string&amp; type, double param) &#123;        if (type == &quot;circle&quot;) &#123;            return std::make_shared&lt;Circle&gt;(param);        &#125; else if (type == &quot;square&quot;) &#123;            return std::make_shared&lt;Square&gt;(param);        &#125;        throw std::invalid_argument(&quot;Unknown shape type&quot;);    &#125;);&#125;
其他高级特性
1. 属性装饰器
#include &lt;pybind11/pybind11.h&gt;#include &lt;string&gt;namespace py = pybind11;class Person &#123;public:    Person(const std::string&amp; name, int age)         : name(name), age(age) &#123;&#125;        std::string get_name() const &#123; return name; &#125;    void set_name(const std::string&amp; new_name) &#123; name = new_name; &#125;        int get_age() const &#123; return age; &#125;    void set_age(int new_age) &#123;         if (new_age &lt; 0) throw std::invalid_argument(&quot;年龄不能为负数&quot;);        age = new_age;    &#125;        std::string get_full_info() const &#123;        return name + &quot;, &quot; + std::to_string(age) + &quot;岁&quot;;    &#125;    private:    std::string name;    int age;&#125;;PYBIND11_MODULE(example, m) &#123;    py::class_&lt;Person&gt;(m, &quot;Person&quot;)        .def(py::init&lt;const std::string&amp;, int&gt;())                // 可读可写属性        .def_property(&quot;name&quot;, &amp;Person::get_name, &amp;Person::set_name)                // 带验证的属性        .def_property(&quot;age&quot;, &amp;Person::get_age, &amp;Person::set_age)                // 只读属性        .def_property_readonly(&quot;full_info&quot;, &amp;Person::get_full_info)                // 静态属性        .def_readwrite_static(&quot;species&quot;, []() &#123; return &quot;人类&quot;; &#125;);&#125;
2. 方法装饰器
#include &lt;pybind11/pybind11.h&gt;#include &lt;pybind11/functional.h&gt;#include &lt;string&gt;#include &lt;chrono&gt;namespace py = pybind11;// 计时装饰器template &lt;typename Func&gt;auto timing_decorator(Func&amp;&amp; func) &#123;    return [func = std::forward&lt;Func&gt;(func)](auto&amp;&amp;... args) &#123;        auto start = std::chrono::high_resolution_clock::now();                auto result = func(std::forward&lt;decltype(args)&gt;(args)...);                auto end = std::chrono::high_resolution_clock::now();        auto duration = std::chrono::duration_cast&lt;std::chrono::microseconds&gt;(            end - start        );                py::print(&quot;函数执行时间:&quot;, duration.count(), &quot;微秒&quot;);        return result;    &#125;;&#125;// 缓存装饰器template &lt;typename Func&gt;auto cache_decorator(Func&amp;&amp; func) &#123;    std::unordered_map&lt;std::string, py::object&gt; cache;        return [func = std::forward&lt;Func&gt;(func), cache = std::move(cache)](        const std::string&amp; key, auto&amp;&amp;... args    ) mutable &#123;        if (cache.find(key) != cache.end()) &#123;            py::print(&quot;缓存命中:&quot;, key);            return cache[key];        &#125;                py::object result = func(key, std::forward&lt;decltype(args)&gt;(args)...);        cache[key] = result;        py::print(&quot;缓存未命中，计算结果:&quot;, key);        return result;    &#125;;&#125;PYBIND11_MODULE(example, m) &#123;    // 原始函数    auto expensive_function = [](const std::string&amp; data) &#123;        // 模拟耗时计算        std::this_thread::sleep_for(std::chrono::milliseconds(100));        return &quot;处理结果: &quot; + data;    &#125;;        // 使用装饰器    m.def(&quot;process_data&quot;, timing_decorator(expensive_function));    m.def(&quot;cached_process&quot;, cache_decorator(expensive_function));&#125;
3. 自定义异常
#include &lt;pybind11/pybind11.h&gt;#include &lt;stdexcept&gt;#include &lt;string&gt;namespace py = pybind11;// 自定义异常类型class ValidationError : public std::runtime_error &#123;public:    ValidationError(const std::string&amp; message)         : std::runtime_error(message) &#123;&#125;&#125;;class DatabaseError : public std::runtime_error &#123;public:    DatabaseError(const std::string&amp; message, int code)         : std::runtime_error(message), error_code(code) &#123;&#125;        int get_code() const &#123; return error_code; &#125;    private:    int error_code;&#125;;PYBIND11_MODULE(example, m) &#123;    // 绑定自定义异常    auto validation_error = py::register_exception&lt;ValidationError&gt;(m, &quot;ValidationError&quot;);    auto db_error = py::register_exception&lt;DatabaseError&gt;(m, &quot;DatabaseError&quot;);        // 为异常添加额外方法    db_error.def(&quot;get_code&quot;, &amp;DatabaseError::get_code);        // 使用自定义异常的函数    m.def(&quot;validate_age&quot;, [](int age) &#123;        if (age &lt; 0) &#123;            throw ValidationError(&quot;年龄不能为负数&quot;);        &#125;        if (age &gt; 150) &#123;            throw ValidationError(&quot;年龄不能超过150&quot;);        &#125;    &#125;);        m.def(&quot;connect_to_db&quot;, [](const std::string&amp; host) &#123;        if (host.empty()) &#123;            throw DatabaseError(&quot;数据库主机不能为空&quot;, 1001);        &#125;        if (host != &quot;localhost&quot;) &#123;            throw DatabaseError(&quot;连接失败: 未知主机&quot;, 1002);        &#125;        return &quot;连接成功&quot;;    &#125;);&#125;
