#include &lt;pybind11/pybind11.h&gt;#include &lt;pybind11/functional.h&gt;#include &lt;iostream&gt;#include &lt;chrono&gt;namespace py = pybind11;class Timer &#123;public:    Timer() : start_time(std::chrono::high_resolution_clock::now()) &#123;        std::cout &lt;&lt; &quot;计时器开始&quot; &lt;&lt; std::endl;    &#125;        ~Timer() &#123;        auto end_time = std::chrono::high_resolution_clock::now();        auto duration = std::chrono::duration_cast&lt;std::chrono::milliseconds&gt;(            end_time - start_time        );        std::cout &lt;&lt; &quot;计时器结束，耗时: &quot; &lt;&lt; duration.count() &lt;&lt; &quot;毫秒&quot; &lt;&lt; std::endl;    &#125;        void reset() &#123;        start_time = std::chrono::high_resolution_clock::now();        std::cout &lt;&lt; &quot;计时器重置&quot; &lt;&lt; std::endl;    &#125;    private:    std::chrono::high_resolution_clock::time_point start_time;&#125;;PYBIND11_MODULE(example, m) &#123;    py::class_&lt;Timer&gt;(m, &quot;Timer&quot;)        .def(py::init&lt;&gt;())        .def(&quot;reset&quot;, &amp;Timer::reset)                // 实现上下文管理器接口        .def(&quot;__enter__&quot;, [](Timer&amp; self) &#123;            return &amp;self;        &#125;)        .def(&quot;__exit__&quot;, [](Timer&amp; self, py::object type, py::object value, py::object traceback) &#123;            // __exit__方法的返回值表示是否处理异常            return py::none();        &#125;);&#125;
