#include &lt;pybind11/pybind11.h&gt;#include &lt;vector&gt;#include &lt;string&gt;namespace py = pybind11;class NumberSequence &#123;public:    NumberSequence(int start, int end)         : start_(start), end_(end), current_(start) &#123;&#125;        // 迭代器接口    int next() &#123;        if (current_ &gt; end_) &#123;            throw py::stop_iteration();        &#125;        return current_++;    &#125;        // 重置迭代器    void reset() &#123;        current_ = start_;    &#125;    private:    int start_;    int end_;    int current_;&#125;;PYBIND11_MODULE(example, m) &#123;    py::class_&lt;NumberSequence&gt;(m, &quot;NumberSequence&quot;)        .def(py::init&lt;int, int&gt;())        .def(&quot;reset&quot;, &amp;NumberSequence::reset)                // 实现迭代器协议        .def(&quot;__iter__&quot;, [](NumberSequence&amp; self) &#123;            self.reset();            return &amp;self;        &#125;)        .def(&quot;__next__&quot;, &amp;NumberSequence::next);        // 范围迭代器示例    m.def(&quot;range_generator&quot;, [](int n) &#123;        return py::make_iterator(range(0, n).begin(), range(0, n).end());    &#125;, py::keep_alive&lt;0, 1&gt;());&#125;
