数据操作（CRUD）
创建数据（Create）
// src/api/user.jsimport &#123; getDB &#125; from &#x27;@/utils/indexedDB&#x27;export const createUser = async (userData) =&gt; &#123;  const db = await getDB()    try &#123;    // 使用add方法添加新数据（主键不存在）    const userId = await db.add(&#x27;users&#x27;, &#123;      ...userData,      createdAt: new Date().toISOString(),      updatedAt: new Date().toISOString()    &#125;)        return &#123; success: true, userId &#125;  &#125; catch (error) &#123;    console.error(&#x27;创建用户失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;export const createOrUpdateUser = async (userData) =&gt; &#123;  const db = await getDB()    try &#123;    // 使用put方法添加或更新数据    const userId = await db.put(&#x27;users&#x27;, &#123;      ...userData,      updatedAt: new Date().toISOString()    &#125;)        return &#123; success: true, userId &#125;  &#125; catch (error) &#123;    console.error(&#x27;创建或更新用户失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;
