// 获取单个用户export const getUserById = async (userId) =&gt; &#123;  const db = await getDB()    try &#123;    const user = await db.get(&#x27;users&#x27;, userId)    return &#123; success: true, data: user &#125;  &#125; catch (error) &#123;    console.error(&#x27;获取用户失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 获取所有用户export const getAllUsers = async () =&gt; &#123;  const db = await getDB()    try &#123;    const users = await db.getAll(&#x27;users&#x27;)    return &#123; success: true, data: users &#125;  &#125; catch (error) &#123;    console.error(&#x27;获取所有用户失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 获取指定数量的用户export const getUsersByLimit = async (limit, offset = 0) =&gt; &#123;  const db = await getDB()    try &#123;    const users = await db.getAll(&#x27;users&#x27;, undefined, limit, offset)    return &#123; success: true, data: users &#125;  &#125; catch (error) &#123;    console.error(&#x27;获取用户列表失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 获取用户总数export const getUserCount = async () =&gt; &#123;  const db = await getDB()    try &#123;    const count = await db.count(&#x27;users&#x27;)    return &#123; success: true, count &#125;  &#125; catch (error) &#123;    console.error(&#x27;获取用户总数失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;
更新数据（Update）
export const updateUser = async (userId, updateData) =&gt; &#123;  const db = await getDB()    try &#123;    // 先获取当前用户数据    const user = await db.get(&#x27;users&#x27;, userId)        if (!user) &#123;      return &#123; success: false, error: &#x27;用户不存在&#x27; &#125;    &#125;        // 更新用户数据    const updatedUser = &#123;      ...user,      ...updateData,      updatedAt: new Date().toISOString()    &#125;        await db.put(&#x27;users&#x27;, updatedUser)    return &#123; success: true, data: updatedUser &#125;  &#125; catch (error) &#123;    console.error(&#x27;更新用户失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;
