事务处理
基本事务操作
// 批量创建用户（使用事务确保原子性）export const batchCreateUsers = async (usersData) =&gt; &#123;  const db = await getDB()    try &#123;    // 开始一个读写事务    const tx = db.transaction(&#x27;users&#x27;, &#x27;readwrite&#x27;)    const store = tx.objectStore(&#x27;users&#x27;)        const results = []    for (const userData of usersData) &#123;      const userId = await store.add(&#123;        ...userData,        createdAt: new Date().toISOString(),        updatedAt: new Date().toISOString()      &#125;)      results.push(userId)    &#125;        // 等待事务完成    await tx.done        return &#123; success: true, userIds: results &#125;  &#125; catch (error) &#123;    console.error(&#x27;批量创建用户失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;
跨存储事务
// 同时操作多个对象仓库export const createOrderWithProducts = async (orderData, productIds) =&gt; &#123;  const db = await getDB()    try &#123;    // 开始一个跨存储的读写事务    const tx = db.transaction([&#x27;orders&#x27;, &#x27;products&#x27;, &#x27;inventory&#x27;], &#x27;readwrite&#x27;)        const orderStore = tx.objectStore(&#x27;orders&#x27;)    const productStore = tx.objectStore(&#x27;products&#x27;)    const inventoryStore = tx.objectStore(&#x27;inventory&#x27;)        // 1. 创建订单    const orderId = await orderStore.add(&#123;      ...orderData,      createdAt: new Date().toISOString(),      status: &#x27;pending&#x27;    &#125;)        // 2. 获取相关产品信息    const products = []    for (const productId of productIds) &#123;      const product = await productStore.get(productId)      if (product) &#123;        products.push(product)                // 3. 更新库存        const inventory = await inventoryStore.get(productId)        if (inventory &amp;&amp; inventory.quantity &gt; 0) &#123;          await inventoryStore.put(&#123;            ...inventory,            quantity: inventory.quantity - 1          &#125;)        &#125; else &#123;          throw new Error(`产品 $&#123;product.name&#125; 库存不足`)        &#125;      &#125;    &#125;        // 等待事务完成    await tx.done        return &#123;      success: true,      orderId,      products    &#125;  &#125; catch (error) &#123;    console.error(&#x27;创建订单失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;
事务错误处理
// 事务错误处理示例export const safeTransaction = async (storeNames, mode, operation) =&gt; &#123;  const db = await getDB()    try &#123;    const tx = db.transaction(storeNames, mode)        // 监听事务错误    tx.onerror = (event) =&gt; &#123;      console.error(&#x27;事务错误:&#x27;, event.target.error)      // 阻止错误冒泡到全局      event.preventDefault()    &#125;        tx.onabort = () =&gt; &#123;      console.log(&#x27;事务被中止&#x27;)    &#125;        tx.oncomplete = () =&gt; &#123;      console.log(&#x27;事务完成&#x27;)    &#125;        const result = await operation(tx)    await tx.done        return &#123; success: true, result &#125;  &#125; catch (error) &#123;    console.error(&#x27;事务操作失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 使用安全事务export const transferPoints = async (fromUserId, toUserId, amount) =&gt; &#123;  return safeTransaction([&#x27;users&#x27;], &#x27;readwrite&#x27;, async (tx) =&gt; &#123;    const store = tx.objectStore(&#x27;users&#x27;)        // 获取用户数据    const fromUser = await store.get(fromUserId)    const toUser = await store.get(toUserId)        if (!fromUser) throw new Error(&#x27;源用户不存在&#x27;)    if (!toUser) throw new Error(&#x27;目标用户不存在&#x27;)    if (fromUser.points &lt; amount) throw new Error(&#x27;积分不足&#x27;)        // 更新积分    await store.put(&#123;      ...fromUser,      points: fromUser.points - amount,      updatedAt: new Date().toISOString()    &#125;)        await store.put(&#123;      ...toUser,      points: toUser.points + amount,      updatedAt: new Date().toISOString()    &#125;)        // 记录交易    const transaction = &#123;      id: Date.now(),      fromUserId,      toUserId,      amount,      timestamp: new Date().toISOString()    &#125;        return transaction  &#125;)&#125;
数据库版本管理
版本升级策略
// src/utils/indexedDB.js - 版本升级示例export const DB_NAME = &#x27;MyVueAppDB&#x27;export const DB_VERSION = 3 // 升级到版本3export const initDB = async () =&gt; &#123;  return openDB(DB_NAME, DB_VERSION, &#123;    upgrade(db, oldVersion, newVersion) &#123;      console.log(`数据库升级: $&#123;oldVersion&#125; -&gt; $&#123;newVersion&#125;`)            // 版本 0 -&gt; 1: 初始版本      if (oldVersion &lt; 1) &#123;        // 创建用户表        const userStore = db.createObjectStore(&#x27;users&#x27;, &#123;           keyPath: &#x27;id&#x27;,          autoIncrement: true         &#125;)                // 创建商品表        db.createObjectStore(&#x27;products&#x27;, &#123; keyPath: &#x27;id&#x27; &#125;)                // 创建设置表        db.createObjectStore(&#x27;settings&#x27;)      &#125;            // 版本 1 -&gt; 2: 添加订单表和索引      if (oldVersion &lt; 2) &#123;        // 创建订单表        const orderStore = db.createObjectStore(&#x27;orders&#x27;, &#123;           keyPath: &#x27;id&#x27;,          autoIncrement: true         &#125;)                // 为订单表创建用户ID索引        orderStore.createIndex(&#x27;userId&#x27;, &#x27;userId&#x27;)        orderStore.createIndex(&#x27;status&#x27;, &#x27;status&#x27;)        orderStore.createIndex(&#x27;createdAt&#x27;, &#x27;createdAt&#x27;)                // 为用户表添加索引        const userStore = db.transaction(&#x27;users&#x27;, &#x27;readwrite&#x27;).objectStore(&#x27;users&#x27;)        userStore.createIndex(&#x27;email&#x27;, &#x27;email&#x27;, &#123; unique: true &#125;)        userStore.createIndex(&#x27;username&#x27;, &#x27;username&#x27;, &#123; unique: true &#125;)      &#125;            // 版本 2 -&gt; 3: 添加库存表和修改用户表结构      if (oldVersion &lt; 3) &#123;        // 创建库存表        db.createObjectStore(&#x27;inventory&#x27;, &#123; keyPath: &#x27;productId&#x27; &#125;)                // 修改用户表结构        const userStore = db.transaction(&#x27;users&#x27;, &#x27;readwrite&#x27;).objectStore(&#x27;users&#x27;)                // 如果用户表没有points字段，添加默认值        userStore.openCursor().then(function cursorIterate(cursor) &#123;          if (!cursor) return                    const user = cursor.value          if (user.points === undefined) &#123;            user.points = 0 // 默认积分            user.level = 1 // 默认等级            cursor.update(user)          &#125;                    return cursor.continue().then(cursorIterate)        &#125;)                // 为用户表添加新的索引        userStore.createIndex(&#x27;level&#x27;, &#x27;level&#x27;)        userStore.createIndex(&#x27;points&#x27;, &#x27;points&#x27;)      &#125;    &#125;  &#125;)&#125;
版本管理最佳实践
// 数据库版本管理工具export const getCurrentDBVersion = async () =&gt; &#123;  try &#123;    const db = await openDB(DB_NAME, 1, &#123; upgrade: () =&gt; &#123;&#125; &#125;)    const version = db.version    db.close()    return version  &#125; catch (error) &#123;    if (error.name === &#x27;NotFoundError&#x27;) &#123;      return 0 // 数据库不存在    &#125;    throw error  &#125;&#125;// 数据库迁移工具export const migrateDB = async (targetVersion) =&gt; &#123;  const currentVersion = await getCurrentDBVersion()    if (currentVersion &gt;= targetVersion) &#123;    console.log(`数据库版本已是 $&#123;currentVersion&#125;，无需迁移`)    return true  &#125;    console.log(`开始数据库迁移: $&#123;currentVersion&#125; -&gt; $&#123;targetVersion&#125;`)    try &#123;    const db = await openDB(DB_NAME, targetVersion, &#123;      upgrade(db, oldVersion, newVersion) &#123;        console.log(`执行升级: $&#123;oldVersion&#125; -&gt; $&#123;newVersion&#125;`)        // 这里可以执行具体的升级逻辑      &#125;    &#125;)        db.close()    console.log(&#x27;数据库迁移完成&#x27;)    return true  &#125; catch (error) &#123;    console.error(&#x27;数据库迁移失败:&#x27;, error)    throw error  &#125;&#125;
