创建索引
// 在数据库升级时创建索引export const initDBWithIndexes = async () =&gt; &#123;  return openDB(&#x27;MyAppDB&#x27;, 2, &#123;    upgrade(db) &#123;      // 用户表索引      if (db.objectStoreNames.contains(&#x27;users&#x27;)) &#123;        const userStore = db.transaction(&#x27;users&#x27;, &#x27;readwrite&#x27;).objectStore(&#x27;users&#x27;)                // 创建普通索引        if (!userStore.indexNames.contains(&#x27;email&#x27;)) &#123;          userStore.createIndex(&#x27;email&#x27;, &#x27;email&#x27;, &#123; unique: true &#125;)        &#125;                // 创建复合索引        if (!userStore.indexNames.contains(&#x27;nameAge&#x27;)) &#123;          userStore.createIndex(&#x27;nameAge&#x27;, [&#x27;name&#x27;, &#x27;age&#x27;])        &#125;                // 创建多字段索引        if (!userStore.indexNames.contains(&#x27;addressCity&#x27;)) &#123;          userStore.createIndex(&#x27;addressCity&#x27;, &#x27;address.city&#x27;)        &#125;      &#125;            // 订单表索引      if (!db.objectStoreNames.contains(&#x27;orders&#x27;)) &#123;        const orderStore = db.createObjectStore(&#x27;orders&#x27;, &#123;           keyPath: &#x27;id&#x27;,          autoIncrement: true         &#125;)                // 创建多个索引        orderStore.createIndex(&#x27;userId&#x27;, &#x27;userId&#x27;)        orderStore.createIndex(&#x27;status&#x27;, &#x27;status&#x27;)        orderStore.createIndex(&#x27;createdAt&#x27;, &#x27;createdAt&#x27;)        orderStore.createIndex(&#x27;totalAmount&#x27;, &#x27;totalAmount&#x27;)                // 创建唯一索引        orderStore.createIndex(&#x27;orderNumber&#x27;, &#x27;orderNumber&#x27;, &#123; unique: true &#125;)      &#125;    &#125;  &#125;)&#125;
使用索引查询
// 通过索引查询export const getUserByEmail = async (email) =&gt; &#123;  const db = await getDB()    try &#123;    const user = await db.getFromIndex(&#x27;users&#x27;, &#x27;email&#x27;, email)    return &#123; success: true, data: user &#125;  &#125; catch (error) &#123;    console.error(&#x27;通过邮箱查询用户失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 通过索引获取多个结果export const getOrdersByUserId = async (userId) =&gt; &#123;  const db = await getDB()    try &#123;    const orders = await db.getAllFromIndex(&#x27;orders&#x27;, &#x27;userId&#x27;, userId)    return &#123; success: true, data: orders &#125;  &#125; catch (error) &#123;    console.error(&#x27;通过用户ID查询订单失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 通过索引获取范围数据export const getHighValueOrders = async (minAmount) =&gt; &#123;  const db = await getDB()    try &#123;    // 获取金额大于等于minAmount的订单    const range = IDBKeyRange.lowerBound(minAmount)    const orders = await db.getAllFromIndex(&#x27;orders&#x27;, &#x27;totalAmount&#x27;, range)        return &#123; success: true, data: orders &#125;  &#125; catch (error) &#123;    console.error(&#x27;查询高价值订单失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;
索引管理
// 索引管理工具export const getIndexNames = async (storeName) =&gt; &#123;  const db = await getDB()    try &#123;    const tx = db.transaction(storeName, &#x27;readonly&#x27;)    const store = tx.objectStore(storeName)    const indexes = Array.from(store.indexNames)        await tx.done    return &#123; success: true, indexes &#125;  &#125; catch (error) &#123;    console.error(`获取$&#123;storeName&#125;索引列表失败:`, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 删除索引export const deleteIndex = async (storeName, indexName) =&gt; &#123;  const db = await getDB()    try &#123;    const tx = db.transaction(storeName, &#x27;readwrite&#x27;)    const store = tx.objectStore(storeName)        if (store.indexNames.contains(indexName)) &#123;      store.deleteIndex(indexName)      await tx.done      return &#123; success: true, message: `索引 $&#123;indexName&#125; 删除成功` &#125;    &#125; else &#123;      return &#123; success: false, error: `索引 $&#123;indexName&#125; 不存在` &#125;    &#125;  &#125; catch (error) &#123;    console.error(`删除索引 $&#123;indexName&#125; 失败:`, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;
