// 区间查找工具export const getUsersByAgeRange = async (minAge, maxAge) =&gt; &#123;  const db = await getDB()    try &#123;    // 创建区间对象    const ageRange = IDBKeyRange.bound(minAge, maxAge)        // 通过索引查询区间数据    const users = await db.getAllFromIndex(&#x27;users&#x27;, &#x27;age&#x27;, ageRange)        return &#123; success: true, data: users &#125;  &#125; catch (error) &#123;    console.error(&#x27;按年龄区间查询用户失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 复杂区间查询export const getRecentOrdersByStatus = async (status, days = 7) =&gt; &#123;  const db = await getDB()    try &#123;    const date = new Date()    date.setDate(date.getDate() - days)    const minDate = date.toISOString()        // 使用复合查询：状态和日期范围    const tx = db.transaction(&#x27;orders&#x27;, &#x27;readonly&#x27;)    const store = tx.objectStore(&#x27;orders&#x27;)        // 先按状态筛选    const statusIndex = store.index(&#x27;status&#x27;)    const statusRange = IDBKeyRange.only(status)        const orders = []        // 使用游标遍历结果并进一步筛选日期    await new Promise((resolve, reject) =&gt; &#123;      const cursorRequest = statusIndex.openCursor(statusRange)            cursorRequest.onsuccess = (event) =&gt; &#123;        const cursor = event.target.result        if (cursor) &#123;          const order = cursor.value          if (order.createdAt &gt;= minDate) &#123;            orders.push(order)          &#125;          cursor.continue()        &#125; else &#123;          resolve()        &#125;      &#125;            cursorRequest.onerror = (event) =&gt; &#123;        reject(event.target.error)      &#125;    &#125;)        await tx.done    return &#123; success: true, data: orders &#125;  &#125; catch (error) &#123;    console.error(&#x27;查询最近订单失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;
