// 使用游标遍历所有数据export const iterateAllUsers = async (callback) =&gt; &#123;  const db = await getDB()    try &#123;    const tx = db.transaction(&#x27;users&#x27;, &#x27;readonly&#x27;)    const store = tx.objectStore(&#x27;users&#x27;)        await new Promise((resolve, reject) =&gt; &#123;      const cursorRequest = store.openCursor()            cursorRequest.onsuccess = (event) =&gt; &#123;        const cursor = event.target.result        if (cursor) &#123;          // 调用回调函数处理当前记录          const shouldContinue = callback(cursor.value, cursor.key)                    if (shouldContinue !== false) &#123;            cursor.continue()          &#125; else &#123;            resolve() // 停止遍历          &#125;        &#125; else &#123;          resolve() // 遍历完成        &#125;      &#125;            cursorRequest.onerror = (event) =&gt; &#123;        reject(event.target.error)      &#125;    &#125;)        await tx.done    return &#123; success: true &#125;  &#125; catch (error) &#123;    console.error(&#x27;遍历用户数据失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 使用游标进行分页查询export const getUsersByPage = async (page = 1, pageSize = 20) =&gt; &#123;  const db = await getDB()    try &#123;    const tx = db.transaction(&#x27;users&#x27;, &#x27;readonly&#x27;)    const store = tx.objectStore(&#x27;users&#x27;)        const users = []    const totalCount = await store.count()    const totalPages = Math.ceil(totalCount / pageSize)    const skip = (page - 1) * pageSize        let count = 0        await new Promise((resolve, reject) =&gt; &#123;      const cursorRequest = store.openCursor()            cursorRequest.onsuccess = (event) =&gt; &#123;        const cursor = event.target.result        if (cursor) &#123;          if (count &gt;= skip &amp;&amp; count &lt; skip + pageSize) &#123;            users.push(cursor.value)          &#125;                    count++                    if (count &lt; skip + pageSize) &#123;            cursor.continue()          &#125; else &#123;            resolve()          &#125;        &#125; else &#123;          resolve()        &#125;      &#125;            cursorRequest.onerror = (event) =&gt; &#123;        reject(event.target.error)      &#125;    &#125;)        await tx.done        return &#123;      success: true,      data: users,      pagination: &#123;        page,        pageSize,        totalCount,        totalPages      &#125;    &#125;  &#125; catch (error) &#123;    console.error(&#x27;分页查询用户失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;
