// src/composables/useIndexedDB.jsimport &#123; ref, reactive, toRefs &#125; from &#x27;vue&#x27;import &#123; getDB &#125; from &#x27;@/utils/indexedDB&#x27;export const useIndexedDB = (storeName) =&gt; &#123;  const state = reactive(&#123;    data: null,    loading: false,    error: null,    pagination: &#123;      page: 1,      pageSize: 20,      totalCount: 0,      totalPages: 0    &#125;  &#125;)  const resetState = () =&gt; &#123;    state.data = null    state.loading = false    state.error = null  &#125;  const getById = async (id) =&gt; &#123;    resetState()    state.loading = true    try &#123;      const db = await getDB()      const data = await db.get(storeName, id)      state.data = data      return &#123; success: true, data &#125;    &#125; catch (error) &#123;      state.error = error.message      console.error(`获取$&#123;storeName&#125;数据失败:`, error)      return &#123; success: false, error: error.message &#125;    &#125; finally &#123;      state.loading = false    &#125;  &#125;  const getAll = async () =&gt; &#123;    resetState()    state.loading = true    try &#123;      const db = await getDB()      const data = await db.getAll(storeName)      state.data = data      state.pagination.totalCount = data.length      state.pagination.totalPages = 1      return &#123; success: true, data &#125;    &#125; catch (error) &#123;      state.error = error.message      console.error(`获取所有$&#123;storeName&#125;数据失败:`, error)      return &#123; success: false, error: error.message &#125;    &#125; finally &#123;      state.loading = false    &#125;  &#125;  const getByPage = async (page = 1, pageSize = 20) =&gt; &#123;    resetState()    state.loading = true    try &#123;      const db = await getDB()      const tx = db.transaction(storeName, &#x27;readonly&#x27;)      const store = tx.objectStore(storeName)      const totalCount = await store.count()      const totalPages = Math.ceil(totalCount / pageSize)      const skip = (page - 1) * pageSize      const data = []      let count = 0      await new Promise((resolve, reject) =&gt; &#123;        const cursorRequest = store.openCursor()        cursorRequest.onsuccess = (event) =&gt; &#123;          const cursor = event.target.result          if (cursor) &#123;            if (count &gt;= skip &amp;&amp; count &lt; skip + pageSize) &#123;              data.push(cursor.value)            &#125;            count++            if (count &lt; skip + pageSize) &#123;              cursor.continue()            &#125; else &#123;              resolve()            &#125;          &#125; else &#123;            resolve()          &#125;        &#125;        cursorRequest.onerror = (event) =&gt; &#123;          reject(event.target.error)        &#125;      &#125;)      await tx.done      state.data = data      state.pagination = &#123;        page,        pageSize,        totalCount,        totalPages      &#125;      return &#123;        success: true,        data,        pagination: state.pagination      &#125;    &#125; catch (error) &#123;      state.error = error.message      console.error(`分页获取$&#123;storeName&#125;数据失败:`, error)      return &#123; success: false, error: error.message &#125;    &#125; finally &#123;      state.loading = false    &#125;  &#125;  const create = async (item) =&gt; &#123;    state.loading = true    try &#123;      const db = await getDB()      const id = await db.add(storeName, item)      return &#123; success: true, id &#125;    &#125; catch (error) &#123;      state.error = error.message      console.error(`创建$&#123;storeName&#125;数据失败:`, error)      return &#123; success: false, error: error.message &#125;    &#125; finally &#123;      state.loading = false    &#125;  &#125;  const update = async (id, item) =&gt; &#123;    state.loading = true    try &#123;      const db = await getDB()      await db.put(storeName, &#123; ...item, id &#125;)      return &#123; success: true &#125;    &#125; catch (error) &#123;      state.error = error.message      console.error(`更新$&#123;storeName&#125;数据失败:`, error)      return &#123; success: false, error: error.message &#125;    &#125; finally &#123;      state.loading = false    &#125;  &#125;  const remove = async (id) =&gt; &#123;    state.loading = true    try &#123;      const db = await getDB()      await db.delete(storeName, id)      return &#123; success: true &#125;    &#125; catch (error) &#123;      state.error = error.message      console.error(`删除$&#123;storeName&#125;数据失败:`, error)      return &#123; success: false, error: error.message &#125;    &#125; finally &#123;      state.loading = false    &#125;  &#125;  const clear = async () =&gt; &#123;    state.loading = true    try &#123;      const db = await getDB()      await db.clear(storeName)      state.data = null      state.pagination.totalCount = 0      state.pagination.totalPages = 0      return &#123; success: true &#125;    &#125; catch (error) &#123;      state.error = error.message      console.error(`清空$&#123;storeName&#125;数据失败:`, error)      return &#123; success: false, error: error.message &#125;    &#125; finally &#123;      state.loading = false    &#125;  &#125;  return &#123;    ...toRefs(state),    getById,    getAll,    getByPage,    create,    update,    remove,    clear,    resetState  &#125;&#125;
