er.email &#125;&#125;&lt;/td&gt;            &lt;td&gt;&#123;&#123; user.age &#125;&#125;&lt;/td&gt;            &lt;td&gt;等级&#123;&#123; user.level &#125;&#125;&lt;/td&gt;            &lt;td&gt;&#123;&#123; user.points &#125;&#125;&lt;/td&gt;            &lt;td class=&quot;actions&quot;&gt;              &lt;button @click=&quot;handleEdit(user)&quot; class=&quot;btn btn-edit&quot;&gt;编辑&lt;/button&gt;              &lt;button @click=&quot;handleDelete(user.id)&quot; class=&quot;btn btn-delete&quot;&gt;删除&lt;/button&gt;            &lt;/td&gt;          &lt;/tr&gt;        &lt;/tbody&gt;      &lt;/table&gt;      &lt;!-- 分页 --&gt;      &lt;div class=&quot;pagination&quot; v-if=&quot;pagination.totalPages &gt; 1&quot;&gt;        &lt;button          @click=&quot;handlePageChange(pagination.page - 1)&quot;          :disabled=&quot;pagination.page === 1&quot;          class=&quot;page-btn&quot;        &gt;          上一页        &lt;/button&gt;        &lt;span class=&quot;page-info&quot;&gt;          第 &#123;&#123; pagination.page &#125;&#125; 页 / 共 &#123;&#123; pagination.totalPages &#125;&#125; 页        &lt;/span&gt;        &lt;button          @click=&quot;handlePageChange(pagination.page + 1)&quot;          :disabled=&quot;pagination.page === pagination.totalPages&quot;          class=&quot;page-btn&quot;        &gt;          下一页        &lt;/button&gt;      &lt;/div&gt;    &lt;/div&gt;    &lt;!-- 创建/编辑用户模态框 --&gt;    &lt;div v-if=&quot;showCreateModal || showEditModal&quot; class=&quot;modal-overlay&quot;&gt;      &lt;div class=&quot;modal&quot;&gt;        &lt;div class=&quot;modal-header&quot;&gt;          &lt;h3&gt;&#123;&#123; showEditModal ? &#x27;编辑用户&#x27; : &#x27;创建用户&#x27; &#125;&#125;&lt;/h3&gt;          &lt;button @click=&quot;closeModal&quot; class=&quot;close-btn&quot;&gt;&amp;times;&lt;/button&gt;        &lt;/div&gt;        &lt;div class=&quot;modal-body&quot;&gt;          &lt;form @submit.prevent=&quot;handleSubmit&quot;&gt;            &lt;div class=&quot;form-group