// 批量操作优化export const batchOperations = async (storeName, operations) =&gt; &#123;  const db = await getDB()    try &#123;    const tx = db.transaction(storeName, &#x27;readwrite&#x27;)    const store = tx.objectStore(storeName)        const results = []        for (const op of operations) &#123;      let result            switch (op.type) &#123;        case &#x27;add&#x27;:          result = await store.add(op.data)          break        case &#x27;put&#x27;:          result = await store.put(op.data)          break        case &#x27;delete&#x27;:          result = await store.delete(op.key)          break        default:          throw new Error(`不支持的操作类型: $&#123;op.type&#125;`)      &#125;            results.push(&#123;        operation: op,        result,        success: true      &#125;)    &#125;        await tx.done    return &#123; success: true, results &#125;  &#125; catch (error) &#123;    console.error(&#x27;批量操作失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 大数据集分页查询优化export const optimizedPagination = async (storeName, page = 1, pageSize = 20, indexName = null, keyRange = null) =&gt; &#123;  const db = await getDB()    try &#123;    const tx = db.transaction(storeName, &#x27;readonly&#x27;)    const store = tx.objectStore(storeName)        let index = store    if (indexName &amp;&amp; store.indexNames.contains(indexName)) &#123;      index = store.index(indexName)    &#125;        // 获取总数（优化：如果有范围查询，先获取范围内的总数）    const totalCount = keyRange       ? await index.count(keyRange)      : await store.count()        const totalPages = Math.ceil(totalCount / pageSize)    const skip = (page - 1) * pageSize        const data = []        // 使用游标进行高效分页    await new Promise((resolve, reject) =&gt; &#123;      let count = 0      const cursorRequest = keyRange         ? index.openCursor(keyRange)        : index.openCursor()            cursorRequest.onsuccess = (event) =&gt; &#123;        const cursor = event.target.result        if (cursor) &#123;          if (count &gt;= skip &amp;&amp; count &lt; skip + pageSize) &#123;            data.push(cursor.value)          &#125;                    count++                    if (count &lt; skip + pageSize) &#123;            cursor.continue()          &#125; else &#123;            resolve()          &#125;        &#125; else &#123;          resolve()        &#125;      &#125;            cursorRequest.onerror = (event) =&gt; &#123;        reject(event.target.error)      &#125;    &#125;)        await tx.done        return &#123;      success: true,      data,      pagination: &#123;        page,        pageSize,        totalCount,        totalPages      &#125;    &#125;  &#125; catch (error) &#123;    console.error(&#x27;优化分页查询失败:&#x27;, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;
