// 内存缓存管理器class CacheManager &#123;  constructor(options = &#123;&#125;) &#123;    this.cache = new Map()    this.ttl = options.ttl || 5 * 60 * 1000 // 默认5分钟    this.maxSize = options.maxSize || 1000  &#125;  set(key, value, ttl = this.ttl) &#123;    // 如果缓存已满，删除最旧的条目    if (this.cache.size &gt;= this.maxSize) &#123;      const firstKey = this.cache.keys().next().value      this.cache.delete(firstKey)    &#125;    const expireTime = Date.now() + ttl    this.cache.set(key, &#123; value, expireTime &#125;)  &#125;  get(key) &#123;    const item = this.cache.get(key)        if (!item) &#123;      return null    &#125;    // 检查是否过期    if (Date.now() &gt; item.expireTime) &#123;      this.cache.delete(key)      return null    &#125;    return item.value  &#125;  delete(key) &#123;    this.cache.delete(key)  &#125;  clear() &#123;    this.cache.clear()  &#125;  has(key) &#123;    return this.get(key) !== null  &#125;&#125;// 创建缓存实例export const cache = new CacheManager(&#123;  ttl: 10 * 60 * 1000, // 10分钟  maxSize: 500&#125;)// 带缓存的数据获取export const getCachedData = async (storeName, key, options = &#123;&#125;) =&gt; &#123;  const cacheKey = `$&#123;storeName&#125;:$&#123;key&#125;`    // 先检查缓存  const cachedData = cache.get(cacheKey)  if (cachedData &amp;&amp; !options.forceRefresh) &#123;    return &#123; success: true, data: cachedData, fromCache: true &#125;  &#125;  // 缓存未命中或需要强制刷新，从数据库获取  try &#123;    const result = await withConnection(async (db) =&gt; &#123;      return db.get(storeName, key)    &#125;)    if (result) &#123;      // 更新缓存      cache.set(cacheKey, result, options.ttl)      return &#123; success: true, data: result, fromCache: false &#125;    &#125;    return &#123; success: false, error: &#x27;数据不存在&#x27; &#125;  &#125; catch (error) &#123;    console.error(`获取$&#123;storeName&#125;数据失败:`, error)    return &#123; success: false, error: error.message &#125;  &#125;&#125;// 缓存失效机制export const invalidateCache = (storeName, key = null) =&gt; &#123;  if (key) &#123;    cache.delete(`$&#123;storeName&#125;:$&#123;key&#125;`)  &#125; else &#123;    // 清除整个store的缓存    for (const cacheKey of cache.cache.keys()) &#123;      if (cacheKey.startsWith(`$&#123;storeName&#125;:`)) &#123;        cache.delete(cacheKey)      &#125;    &#125;  &#125;&#125;// 更新数据时自动失效缓存export const updateWithCacheInvalidation = async (storeName, id, data) =&gt; &#123;  const result = await withConnection(async (db) =&gt; &#123;    return db.put(storeName, data, id)  &#125;)  if (result.success) &#123;    // 失效相关缓存    invalidateCache(storeName, id)    // 可以根据需要失效其他相关缓存  &#125;  return result&#125;
