// 批量操作队列class BatchOperationQueue &#123;  constructor(options = &#123;&#125;) &#123;    this.queue = new Map()    this.batchSize = options.batchSize || 100    this.delay = options.delay || 100    this.timers = new Map()  &#125;  addOperation(storeName, operation) &#123;    if (!this.queue.has(storeName)) &#123;      this.queue.set(storeName, [])    &#125;    const storeQueue = this.queue.get(storeName)    storeQueue.push(operation)    // 如果达到批处理大小，立即执行    if (storeQueue.length &gt;= this.batchSize) &#123;      this.processBatch(storeName)    &#125; else if (!this.timers.has(storeName)) &#123;      // 否则设置延迟执行      this.timers.set(storeName, setTimeout(() =&gt; &#123;        this.processBatch(storeName)      &#125;, this.delay))    &#125;  &#125;  async processBatch(storeName) &#123;    // 清除定时器    if (this.timers.has(storeName)) &#123;      clearTimeout(this.timers.get(storeName))      this.timers.delete(storeName)    &#125;    const storeQueue = this.queue.get(storeName)    if (!storeQueue || storeQueue.length === 0) &#123;      return    &#125;    try &#123;      // 执行批处理操作      await batchOperations(storeName, storeQueue)            // 清空队列      this.queue.set(storeName, [])    &#125; catch (error) &#123;      console.error(`批处理操作失败 ($&#123;storeName&#125;):`, error)      // 可以选择重试或处理失败的操作    &#125;  &#125;  async flush() &#123;    // 立即执行所有队列中的操作    for (const storeName of this.queue.keys()) &#123;      await this.processBatch(storeName)    &#125;  &#125;&#125;// 创建批处理队列实例export const batchQueue = new BatchOperationQueue(&#123;  batchSize: 50,  delay: 50&#125;)// 使用批处理队列的操作export const queueUserUpdate = (userId, data) =&gt; &#123;  batchQueue.addOperation(&#x27;users&#x27;, &#123;    type: &#x27;put&#x27;,    data: &#123; id: userId, ...data &#125;  &#125;)&#125;export const queueProductAdd = (productData) =&gt; &#123;  batchQueue.addOperation(&#x27;products&#x27;, &#123;    type: &#x27;add&#x27;,    data: productData  &#125;)&#125;
